import React, {Component} from 'react';
import {View, TextInput, Button, Alert} from 'react-native';
import Resultado from '../components/Resultado';
import Spinner from '../components/Spinner';
import Splash from './Splash';
import Background from './Background';
import verificarCUIL from '../functions/VerificarCUIL';
import consultarCUIL from '../api/ConsultarCUIL';
import LoginCUIL from '../api/LoginCUIL';

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cuil: null,
      verGrilla: false,
      cuilValido: false,
      resultado: null,
      nombre: '',
      loading: false,
      showSplash: false,
    };
  }
  componentDidMount() {
    /*setTimeout(() => {
      this.setState({showSplash: false});
    }, 5000);*/
  }

  render() {
    return (
      <>
        {this.state.showSplash === true ? (
          <Splash />
        ) : (
          <View>
            {this.state.loading === false ? (
              <>
                <TextInput
                  // eslint-disable-next-line react-native/no-inline-styles
                  style={{borderWidth: 1}}
                  onChangeText={this.changeCUIL}
                  value={this.state.cuil}
                  placeholder="Ingrese CUIL"
                  keyboardType={'numeric'}
                />
                <Button onPress={this.onPress} title="Ingresar" />
                <Resultado
                  cuil={this.state.cuil}
                  resultado={this.state.resultado}
                  nombre={this.state.nombre}
                />
                <Background />
              </>
            ) : (
              <Spinner />
            )}
          </View>
        )}
      </>
    );
  }

  onPress = async () => {
    this.setState({loading: !this.state.loading});
    const esCUILValido = await verificarCUIL(this.state.cuil);
    switch (esCUILValido) {
      case false:
        this.setState({loading: !this.state.loading});
        Alert.alert('CUIL inválido');
        break;

      case true:
        this.setState({
          cuilValido: true,
        });
        //const resp = await consultarCUIL(this.state.cuil);
        const resp = await LoginCUIL(this.state.cuil);
        switch (resp.nombre) {
          default:
            this.setState({
              resultado: resp.Resultado,
              nombre: resp.Nombre,
              loading: !this.state.loading,
            });
            this.props.handleLogin(resp.Nombre);
            break;
        }
        break;
    }
  };

  changeCUIL = cuil => {
    this.setState({
      cuil: cuil,
      resultado: null,
      nombre: '',
      cuilValido: false,
    });
  };
}
